from celery import Celery, chain, groupimport timeapp = Celery('celery_test', broker='amqp://guest@localhost//', backend='rpc://')@app.task()def add(a, b):    time.sleep(4)    return a / b@app.task()def sub(a, b):    return a + bresult = chain(add.s(3, 4), sub.s(6))print(result().get())print(result().parent.get())# نتیجه ای که از اولی میاید به بعدی میدهد به همین دلیل در دومی فقط یک عدد دادیم# ________________________________________________________________________result_2 = group(add.s(3, 1), sub.s(5, 6))print(result().get())