from celery import Celeryimport timeapp = Celery('celery_test', broker='amqp://guest@localhost//', backend='rpc://')# کار Signatures بسته بندی کردن task ها است.# 3 حالت مختلف دارند : Partials , Immutability , Callbacks@app.task()def add(a, b):    time.sleep(4)    return a / b@app.task()def sub(a, b):    return a + bresult = add.signature((4, 5), coundown=10)result_2 = add.apply_async((4, 5), link=sub.signature((1,)))