from celery import Celeryfrom celery.utils.log import get_task_loggerapp = Celery('first', broker='amqp://guest@localhost//', backend='rpc://')logger = get_task_logger(__name__)@app.task(name='celery_test.adding', bind=True, default_retry_delay=600)def add(self, a, b):    try:        return a / b    except ZeroDivisionError:        logger.info('Sorry...')        self.retry(countdown=10, max_retries=2)"""------------------------python console------------------------------result = add.delay(4, 4) result.ready() == True or Falseresult.get()   == 1if result = add.delay(4, 0) : result.get()   == ???? but : result.get(propagate=False) == error =!"""