from celery import Celery, signalsapp = Celery('celery_test', broker='amqp://guest@localhost//', backend='rpc://')app.config_from_object('celery_conf')@app.taskdef add(a, b):    return a + b@app.taskdef sub(a, b):    return a - b@signals.task_postrun.connectdef show_info(sender=None, **kwargs):    print(sender.request)# ----------------------python console-----------------------# from celery_test import add,sub# add.daly(4,5