from celery import Celeryimport timeapp = Celery('first', broker='amqp://guest@localhost//', backend='rpc://')app.conf.update(    task_time_limit=60,  # after in 60s task kill    task_soft_time_limit=50,  # after in 50s I do    worker_consurrency=8,    worker_prefetch_multiplier=1,    task_ignore_result=True,  # Not save    task_always_eager=True,  # wrong    task_acks_late=True,  #)# برای config ها بهتر است در یک فایل جداگانه ذخیره کنیم@app.task()def add(a, b):    time.sleep(4)    return a / b